import React from 'react';
import Select from 'react-select';
import colleges from '../data/colleges';
import majors from '../data/majors';

function SearchItems(props) {
  const { input1, setInput1, input2, setInput2, handleSubmit, tuitionState, setTuitionState, formSubmitted } = props;

  const collegeOptions = colleges.map((college) => ({
    value: college.name,
    label: college.name,
  }));

  const majorOptions = majors.map((majors) => ({
    value: majors.name,
    label: majors.name,
  }));

  const collegeDefaultValue = formSubmitted
    ? { label: input1, value: input1 }
    : { label: "Enter college name", value: null };

  const majorDefaultValue = formSubmitted
    ? { label: input2, value: input2 }
    : { label: "Enter major name", value: null };

  return (
    <form onSubmit={handleSubmit}>
      <div style={{ display: "flex" }}>
        <label className="input-label">
          <Select
            options={collegeOptions}
            defaultValue={collegeDefaultValue}
            onChange={(option) => setInput1(option.value)}
            styles={{ control: (styles) => ({ ...styles, width: 200, height: 50 }) }}
            menuPlacement="auto"
            menuPosition="fixed"
          />
        </label>
        <label className="input-label">
          <Select
            options={majorOptions}
            defaultValue={majorDefaultValue}
            onChange={(option) => setInput2(option.value)}
            styles={{ control: (styles) => ({ ...styles, width: 200, height: 50 }) }}
            menuPlacement="auto"
            menuPosition="fixed"
          />
        </label>
        <button type="submit">+</button>
      </div>
      <div className="radio-buttons">
        <label>
          <input
            type="radio"
            value="In State"
            checked={tuitionState === "In State"}
            onChange={() => setTuitionState("In State")}
          />
          In State
        </label>
        <label>
          <input
            type="radio"
            value="Out of State"
            checked={tuitionState === "Out of State"}
            onChange={() => setTuitionState("Out of State")}
          />
          Out of State
        </label>
      </div>
    </form>
  );
}


export default SearchItems;
