import React, { useState } from "react";
import InputTable from "./components/InputTable";
import School_to_Codes from "./data/School_to_Codes"; // import the School_to_Codes module
import "./App.css";

function App() {
  const [input1, setInput1] = useState("");
  const [input2, setInput2] = useState("");
  const [tableData, setTableData] = useState([]);
  const [tuitionState, setTuitionState] = useState("In State"); // default value for tuitionState

  const handleSubmit = ({ input1, input2 }) => {
    // Look up the college code in the School_to_Codes dictionary
    const collegeCode = School_to_Codes.School_2_Codes_Dict[input1];

    // Print the key-value pair to the console
    console.log(`${input1}: ${collegeCode}`);

    // Add the input data to the table
    setTableData([...tableData, { input1, input2, tuitionState }]);
    setInput1("");
    setInput2("");
    setTuitionState("In State"); // reset tuitionState to default
  };

  return (
    <div>
      <InputTable
        input1={input1}
        setInput1={setInput1}
        input2={input2}
        setInput2={setInput2}
        tableData={tableData}
        onSubmit={handleSubmit}
        tuitionState={tuitionState}
        setTuitionState={setTuitionState}
      />
    </div>
  );
}

export default App;
