import React from "react";

function InputTable(props) {
  const { input1, setInput1, input2, setInput2, tableData, setTableData } = props;

  const handleSubmit = (e) => {
    e.preventDefault();
    props.onSubmit({ input1, input2 });
    setInput1("");
    setInput2("");
  };

  const handleRemove = (indexToRemove) => {
    setTableData(tableData.filter((_, index) => index !== indexToRemove));
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div style={{ display: "flex" }}>
          <label>
            College:
            <input type="text" value={input1} onChange={(e) => setInput1(e.target.value)} />
          </label>
          <label>
            Major:
            <input type="text" value={input2} onChange={(e) => setInput2(e.target.value)} />
          </label>
          <button type="submit">+</button>
        </div>
      </form>
      <br />
      <table>
        <thead>
          <tr>
            <th>College</th>
            <th>Major</th>
            <th>Salary</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tableData.map((data, index) => (
            <tr key={index}>
              <td>{data.input1}</td>
              <td>{data.input2}</td>
              <td>{"Salary xxx"}</td>
              <td>
                <button style={{ backgroundColor: "red", color: "white" }} onClick={() => handleRemove(index)}>
                  -
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default InputTable;
