import React, { useState } from 'react';
import Select from 'react-select';
import colleges from '../data/colleges';
import majors from '../data/majors';

function SearchItems(props) {
  const { input1, setInput1, input2, setInput2, handleSubmit, tuitionState, setTuitionState } = props;

  const collegeOptions = colleges.map((college) => ({
    value: college.name,
    label: college.name,
  }));

  const majorOptions = majors.map((major) => ({
    value: major.name,
    label: major.name,
  }));

  const [collegeValue, setCollegeValue] = useState(null);
  const [majorValue, setMajorValue] = useState(null);

  const resetInputs = () => {
    setInput1("");
    setInput2("");
    setCollegeValue(null);
    setMajorValue(null);
  };

  const handleSubmission = (event) => {
    event.preventDefault();
    handleSubmit({ input1, input2 });
    resetInputs();
  };

  return (
    <form onSubmit={handleSubmission}>
      <div style={{ display: "flex" }}>
        <label className="input-label">
          <Select
            options={collegeOptions}
            value={collegeValue}
            onChange={(option) => {
              setInput1(option.value);
              setCollegeValue(option);
            }}
            styles={{ control: (styles) => ({ ...styles, width: 200, height: 50 }) }}
            menuPlacement="auto"
            menuPosition="fixed"
            placeholder="Enter college name"
          />
        </label>
        <label className="input-label">
          <Select
            options={majorOptions}
            value={majorValue}
            onChange={(option) => {
              setInput2(option.value);
              setMajorValue(option);
            }}
            styles={{ control: (styles) => ({ ...styles, width: 200, height: 50 }) }}
            menuPlacement="auto"
            menuPosition="fixed"
            placeholder="Enter major name"
          />
        </label>
        <button type="submit">+</button>
      </div>
      <div className="radio-buttons">
        <label>
          <input
            type="radio"
            value="In State"
            checked={tuitionState === "In State"}
            onChange={() => setTuitionState("In State")}
          />
          In State
        </label>
        <label>
          <input
            type="radio"
            value="Out of State"
            checked={tuitionState === "Out of State"}
            onChange={() => setTuitionState("Out of State")}
          />
          Out of State
        </label>
      </div>
    </form>
  );
}

export default SearchItems;
