import React from 'react';
import Select, { components } from 'react-select';
import colleges from './colleges';
import majors from './majors';

const MenuList = ({ children, maxHeight }) => {
  const itemCount = React.Children.count(children);
  const height = itemCount < 8 ? itemCount * 40 : maxHeight;
  return (
    <components.MenuList maxHeight={height}>
      {children}
    </components.MenuList>
  );
};

function SearchItems(props) {
  const { input1, setInput1, input2, setInput2, handleSubmit, tuitionState, setTuitionState } = props;

  const collegeOptions = colleges.map(college => ({
    value: college.name,
    label: college.name
  }));

  const majorOptions = majors.map(majors => ({
    value: majors.name,
    label: majors.name
  }))

  return (
    <form onSubmit={handleSubmit}>
      <div style={{ display: "flex" }}>
        <label className="input-label">
          <Select
            options={collegeOptions}
            defaultValue={{ label: "Enter college name", value: null }}
            onChange={option => setInput1(option.value)}
            styles={{ control: styles => ({ ...styles, width: 200, height: 50 }) }}
            components={{ MenuList }}
            menuIsVirtualized
            menuPosition="fixed"
            maxMenuHeight={200}
          />
        </label>
        <label className="input-label">
          <Select
            options={majorOptions}
            defaultValue={{ label: "Enter major name", value: null }}
            onChange={option => setInput2(option.value)}
            styles={{ control: styles => ({ ...styles, width: 200, height: 50 }) }}
            menuPlacement="auto"
            menuPosition="fixed"
          />
        </label>
        <button type="submit">+</button>
      </div>
      <div className="radio-buttons">
        <label>
          <input type="radio" value="In State" checked={tuitionState === "In State"} onChange={() => setTuitionState("In State")} />
          In State
        </label>
        <label>
          <input type="radio" value="Out of State" checked={tuitionState === "Out of State"} onChange={() => setTuitionState("Out of State")} />
          Out of State
        </label>
      </div>
    </form>
  );
}

export default SearchItems;
