import React, { useState } from "react";
import InputTable from "./components/InputTable";
import "./App.css";

function App() {
  const [input1, setInput1] = useState("");
  const [input2, setInput2] = useState("");
  const [tuitionState, setTuitionState] = useState("In State"); // default value for tuitionState
  const [prevInputs, setPrevInputs] = useState({input1: "", input2: "", tuitionState: "In State"});

  const handleSubmit = ({ input1, input2 }) => {
    setPrevInputs({input1, input2, tuitionState});
    setInput1("");
    setInput2("");
    setTuitionState("In State"); // reset tuitionState to default
  };

  return (
    <div>
      <InputTable
        input1={input1}
        setInput1={setInput1}
        input2={input2}
        setInput2={setInput2}
        tableData={tableData}
        onSubmit={handleSubmit}
        tuitionState={tuitionState}
        setTuitionState={setTuitionState}
        prevInputs={prevInputs}
      />
    </div>
  );
}

export default App;
